import React from 'react'
import ImageCard from '../components/ImageCard';
import {Row, Col} from 'reactstrap'
import axios from 'axios'

class ImageList extends React.Component{
    constructor(props){
        super(props)
        this.state={
            image: null,
            images: null
        }
        this.getImages=this.getImages.bind(this)
    }

    async getImages(){
        const params={
            path: "C:\\Users\\srija\\Desktop\\"+this.state.image
        }
        var images
        if(this.state.image==null){
            await axios.get('http://127.0.0.1:5000/shuffle').then(response => {
                console.log('Data received:', response.data);
                images=response.data.images
              })
              .catch(error => {
                console.error('Error fetching data:', error);
              });
        }
        // console.log(images)

        return images

    }

    render(){
        var images = this.getImages()
        console.log("hi",this.state.images)
        var image=this.state.image
        // var images=this.props.images
        var chunk_images=[]
        var chunkSize=8
        for (let i = 0; i < images.length; i += chunkSize) {
            const chunk = images.slice(i, i + chunkSize);
            chunk_images.push(chunk)
        }
        return(
            <div>
                {chunk_images.map((chunk_image, index1) => (
                    <Row xs="8" key={index1} style={{"padding":"0% 0%", "margin":"0% 5%"}}>
                    {chunk_image.map((image, index )=> (
                        <Col style={{"margin":"1% 1%", "padding":"0.0%"}}>
                        <ImageCard src={"../GPR_dataset"+image}></ImageCard>
                        </Col>
                    ))}
                    </Row>
                ))}
            </div>
        );
    }
}

export default ImageList
